system.macro:
// Gets token ID
const speaker = ChatMessage.getSpeaker();
const tokenId = speaker.token;
const token = canvas.tokens.get(tokenId);

if (!token) {
  ui.notifications.warn("Select the token on the map before using Light.");
  return;
}

const TORCH_FLAG = "torch-light-active";

if (token.document.getFlag("world", TORCH_FLAG)) {
  await token.document.update({
    light: {
      dim: 0,
      bright: 0,
      color: null,
      alpha: null,
      animation: { type: null }
    }
  });
  await token.document.unsetFlag("world", TORCH_FLAG);
} else {
  await token.document.update({
    light: {
      bright: 6,
      color: "#ffaa66",
      alpha: 0.5,
      angle: 360, //change if want it to illuminate just certain directions and stuff
      rotation: token.rotation,
      animation: {
        type: "torch",
        speed: 2,
        intensity: 5
      }
    }
  });
  await token.document.setFlag("world", TORCH_FLAG, true);
}
